mapper.select:
SELECT hd.hnum ,hd.id ,hd.hdate ,hd.tid ,h.playtime ,m.name AS tname ,t.activity AS tactivity FROM HEALTHDATE hd INNER JOIN ( SELECT hnum ,SUM(playtime) AS playtime FROM ( SELECT hnum ,ROUND((endtime - starttime) * 24 * 60) AS playtime FROM HEALTH ) GROUP BY hnum ) h ON hd.hnum = h.hnum LEFT OUTER JOIN MEMBER m ON hd.tid = m.id LEFT OUTER JOIN TRAINER t ON hd.tid = t.id WHERE hd.id = #{id}
AND hd.hdate = #{nowDate}
AND hd.tid = #{tid}
ORDER BY hd.hdate DESC

SELECT 	* 
FROM 	HEALTHDATE 
WHERE 	hdate = #{hdate}
AND		id 	  = #{id}

mapper.update:
UPDATE 	HEALTHDATE 
SET 	hdate = #{hdate}
	   ,tid   = #{tid} 
WHERE 	hnum = #{hnum}

UPDATE	HEALTHDATE 
SET 	tid = #{tid} 
WHERE 	hnum = #{hnum}

