mapper.update:
update tschedule set
tsday=#{tsday}, tstime=#{tstime}, tsdate=#{tsdate}
where tid = #{tid}

mapper.select:
select count(*) from reservation
where mid = #{mid} and tid = #{tid}
and rdate < sysdate and rstate = 'true'

select * from reservation
where mid = #{mid}
order by rdate

mapper.update:
update reservation set
rstate = #{rstate}
where rnum = #{rnum}
