mapper.select:
select count(*)
from review
where tid = #{tid}

	and mid in (select id from member where name like #{keyword})


	and rtitle like #{keyword}

select * from review where tid = #{tid}
and mid in (select id from member where name like #{keyword})
and rtitle like #{keyword}
order by rating desc
order by rating asc
order by rdate desc

select count(*) from review
where mid = #{mid}
and tid = #{tid}

mapper.delete:
delete review 
where tid = #{tid}
and hasReview = 'N'

mapper.update:
update review
set rating = #{rating}, rtitle = #{rtitle}, rcontent = #{rcontent}, rdate = sysdate 
where renum = #{renum}
