mapper.select:
select count(*) from usage
where tid = #{tid}
and mid = #{mid}
and ustate = 'available'

mapper.update:
update usage set
usage = usage -1
where unum = #{unum}

update usage set
usage = usage +1
where unum = #{unum}

update usage set
ustate = 'unavailable'
where unum = #{unum}

update usage set
ustate = 'available'
where unum = #{unum}

mapper.select:
		select * from usage
		where mid = #{mid}
    	and ustate = 'available'

select * from usage
where mid = #{mid}
and ustate = 'available'
